import React,{Component} from 'react';
import Signup from './Signup.js';
import './App.css';

class App extends Component {

   constructor(){

  }
     showsignup = ()=> {
        const currentSignupdetails=this.Signupdetails;
        return this.Signupdetails=!currentSignupdetails;
    } 
    render(){

      const arrayFields=["Email Id","Name","Phone no.","Password","Confirm Password"];
      const arrayFieldstype=["email","text","number","password","password"];
      let Signupdetails=true;
      let details=null;

    if(Signupdetails){
        details=(
            <div>
                {this.arrayFields.map((element,index) => {
                   return <Signup
                    field={arrayFields[index]}
                    type={arrayFieldstype[index]}
                    key={index}
                    />
                })}
            </div>

        )


    }

  return (
    <div className="App">
      <button type="button" onClick={this.showsignup} >Signup</button>
      {details}

    </div>
  );
}}

export default App;
