import React from 'react';
import Signup from './Signup.js';
import './App.css';

function App() {

    const arrayFields=["Email Id","Name","Phone no.","Password","Confirm Password"];
    const arrayFieldstype=["email","text","number","password","password"];
    let Signupdetails=true;
    let details=null;
    function showsignup(){
        const currentSignupdetails=Signupdetails;
        Signupdetails=!currentSignupdetails;
    } 
    if(Signupdetails){
        details=(
            <div>
                {arrayFields.map((element,index) => {
                   return <Signup
                    field={arrayFields[index]}
                    type={arrayFieldstype[index]}
                    key={index}
                    />
                })}
            </div>

        )


    }

  return (
    <div className="App">
      <button type="button" onClick={showsignup()} >Signup</button>
      {details};
      <button type="submit">Submit</button>

    </div>
  );
}

export default App;
