import React from 'react';
import './Signup.css';

export const Messagesignup=(props)=>{
     return(
         <div>{props.message}</div>

     );
 }
 const Signup = () => { 
    // let messagesignup=['','Signed-Up Successfully','Um...Nocheating',"Password and Confirm Password doesn't match"]
    // let messagesignupindex=0;
    let arrayFieldinput=new Array(6);
    arrayFieldinput=[' ',' ',' ',' ',' ', ' '];
    const pushinginputtyped = (field,index)=>{
       
       index===0?arrayFieldinput[0]='': arrayFieldinput[index]=document.getElementById(field).value;
    }  
    const verifysignup = () => {
        // pushinginputtyped("Confirm Password",5);
        if(((arrayFieldinput[4]===arrayFieldinput[5])&&((document.getElementById('Name').value!==' ')&&(document.getElementById('Email-Id').value!==' ')&&(document.getElementById('Phone number').value!==' ')))){
            console.log(document.getElementById('Name').value);
            console.log(document.getElementById('Email-Id').value);
            console.log(document.getElementById('Phone number').value);
            console.log(document.getElementById('Password').value);
            console.log(document.getElementById('Confirm Password').value);
            // messagesignupindex=3;
            alert('Signed-Up Successfully');
          
          
        }
        else{
            console.log(document.getElementById('Name').value);
            console.log(document.getElementById('Email-Id').value);
            console.log(document.getElementById('Phone number').value);
            console.log(document.getElementById('Password').value);
            console.log(document.getElementById('Confirm Password').value);
            console.log(arrayFieldinput[4]);
            console.log(arrayFieldinput[5]);
            
            //  alert('Invalid Credentials,Try again');
        }
    }
      
        
   
    
    return (
        <div >
         <form onSubmit={verifysignup}>
         <div className="signup">
         <p className="text">Name  </p>
         <div className="inputbox">:&nbsp;<input id="Name" type="text" onChange={()=>{pushinginputtyped("Name",0)}}/> </div>
         <p className="text">Email-Id </p>
         <div className="inputbox">:&nbsp;<input id="Email-Id" type="email" onChange={()=>{pushinginputtyped("Email-Id",1)}}/> </div>
         <p className="text">Phone number </p>
         <div className="inputbox">:&nbsp;<input id="Phone number" type="number" onChange={()=>{pushinginputtyped("Phone number",2)}}/> </div>
         <p className="text">Password </p>
         <div className="inputbox">:&nbsp;<input id="Password"  type="password"  onChange={()=>{pushinginputtyped("Password",3)}}/> </div>
         <p className="text">Confirm Password </p>
         <div className="inputbox">:&nbsp;<input  id="Confirm Password" type="password" onChange={()=>{pushinginputtyped("Confirm Password",4)}}/> </div>
         </div>
         <div className="btnsignup" ><input type="submit"/></div>
         </form>
         <div></div>
        </div>
       
    );

}
export default Signup;
