import React from 'react';
import './Signup.css';


 const Signup = () => { 
    let arrayFieldinput=[];
    let inputtyped='';

    const Inputtyped=(e)=>{
        inputtyped=e.target.value
    }

    const pushinginputtyped = ()=>{
        arrayFieldinput.push(inputtyped);
    }

    

    
     
    const showsignup = () => {
        if(inputvaluelength===5){
          alert('Note: it is made for one time use only .Do not use Cookies, enter "each Field" on you own and wait for Correct status untill the "submit" button is pressed');
          this.setState({inputvaluelength:1});
          
        }
        else if(this.state.inputvaluelength===0){
          this.state.inputvalue.shift();
        }
            const currentSignupdetails=this.state.Signupdetails;
            this.setState({Signupdetails:!currentSignupdetails});
        } 
        
   
    
    return (
        <div className="signup">
         <form>
         <p className="text">Name  :</p>
         <div className="inputbox"><input  type="text" onChange={Inputtyped} onClick={pushinginputtyped}/> </div>
         <p className="text">Email-Id :</p>
         <div className="inputbox"><input  type="email" onChange={Inputtyped} onClick={pushinginputtyped}/> </div>
         <p className="text">Phone number :</p>
         <div className="inputbox"><input  type="number" onChange={Inputtyped} onClick={pushinginputtyped}/> </div>
         <p className="text">Password :</p>
         <div className="inputbox"><input  type="text" onChange={Inputtyped} onClick={pushinginputtyped}/> </div>
         <p className="text">Confirm Password :</p>
         <div className="inputbox"><input  type="text" onChange={Inputtyped} onClick={pushinginputtyped}/> </div>
         </form>
        </div>
       
    );

}
export default Signup;
