import React from 'react';
import './Signup.css';


 const Signup = () => { 
    let arrayFieldinput=new Array(6);
    arrayFieldinput=[' ',' ',' ',' ',' ', ' '];
    let inputtyped=new Array(6);

    const Inputtyped=(e,index)=>{
        inputtyped[index]=e.target.value
    }

    const pushinginputtyped = (index)=>{
       
        arrayFieldinput[index]=inputtyped;
    }  
    const verifysignup = () => {
        pushinginputtyped(5);
        if(arrayFieldinput[4]=== arrayFieldinput[5]){
          alert('Signed-Up Successfully');
          
        }
        else{
            alert('Invalid Credentials');
        }
    }
      
        
   
    
    return (
        <div >
         
         <form onSubmit={verifysignup}>
         <div className="signup">
         <p className="text">Name  :</p>
         <div className="inputbox"><input  type="text" onChange={(e)=>{Inputtyped(e,0)}} onClick={()=>{pushinginputtyped(0)}}/> </div>
         <p className="text">Email-Id :</p>
         <div className="inputbox"><input  type="email" onChange={(e)=>{Inputtyped(e,1)}} onClick={()=>{pushinginputtyped(1)}}/> </div>
         <p className="text">Phone number :</p>
         <div className="inputbox"><input  type="number" pattern=".{10,}" onChange={(e)=>{Inputtyped(e,2)}} onClick={()=>{pushinginputtyped(2)}}/> </div>
         <p className="text">Password :</p>
         <div className="inputbox"><input  type="text" onChange={(e)=>{Inputtyped(e,3)}} onClick={()=>{pushinginputtyped(3)}}/> </div>
         <p className="text">Confirm Password :</p>
         <div className="inputbox"><input type="text" onChange={(e)=>{Inputtyped(e,4)}} onClick={()=>{pushinginputtyped(4)}}/> </div>
         </div>
         <div className="btn" ><input type="submit"/></div>
         </form>
        </div>
       
    );

}
export default Signup;
