import React from 'react';
import './Signup.css';


 const Signup = (props) => { 
    let arrayFields=["","Name","Phone no.","Password","Confirm Password"];
    let arrayFieldstype=["email","text","number","password","password"];

    const verificationSignUp = () => {
        if((this.state.inputvalue[this.state.inputvalue.length -2]===this.state.inputvalue[this.state.inputvalue.length-1])&&(this.state.inputvalue.length===5)){
          alert("Signed Up successfully!!");
        }
        if(((this.state.inputvalue[this.state.inputvalue.length -2]!==this.state.inputvalue[this.state.inputvalue.length -1])&&(this.state.inputvalue.length>=2))||this.state.inputvalue.length===0){
          alert("Could not Sign-Up");
        }
        
      }
     
    const showsignup = () => {
        if(this.state.inputvaluelength===5){
          alert('Note: it is made for one time use only .Do not use Cookies, enter "each Field" on you own and wait for Correct status untill the "submit" button is pressed');
          this.setState({inputvaluelength:1});
          
        }
        else if(this.state.inputvaluelength===0){
          this.state.inputvalue.shift();
        }
            const currentSignupdetails=this.state.Signupdetails;
            this.setState({Signupdetails:!currentSignupdetails});
        } 
        
    const showsignIn = () => {
          const currentSignIndetails=this.state.SignIndetails;
          this.setState({SignIndetails:!currentSignIndetails});
      } 
    
    return (
        <div className="signup">
         <p className="text">Name  :</p>
         <div className="inputbox"><input  type="text" onChange={props.changed} onClick={props.click}/> </div>
         <p className="text">Email-Id :</p>
         <div className="inputbox"><input  type="email" onChange={props.changed} onClick={props.click}/> </div>
         <p className="text">Phone number :</p>
         <div className="inputbox"><input  type="number" onChange={props.changed} onClick={props.click}/> </div>
         <p className="text">Password :</p>
         <div className="inputbox"><input  type="text" onChange={props.changed} onClick={props.click}/> </div>
         <p className="text">Confirm Password :</p>
         <div className="inputbox"><input  type="text" onChange={props.changed} onClick={props.click}/> </div>
        </div>
       
    );

}
export default Signup;
