import React from 'react';
import Signup from './Signup.js';
import SignIn from './SignIn.js';
import './App.css';

function app1() {


    let arrayFields=["Email Id","Name","Phone no.","Password","Confirm Password"];
    const  arrayFieldstype=["email","text","number","password","password"];
    let Signupdetails=false;
    let  SignIndetails=false;
    let detailsignup=null;
    let detailsignIn=null;
    

  const showsignup = () => {
       console.log(Signupdetails);
        const currentSignupdetails=Signupdetails;
        Signupdetails=!currentSignupdetails;
        console.log(Signupdetails);
    } 
  const  showsignIn = () => {
    console.log(SignIndetails);
      const currentSignIndetails=SignIndetails;
      SignIndetails=!currentSignIndetails;
      console.log(SignIndetails);
  } 

 
    
    
    if(Signupdetails){
        detailsignup=(
            <div>
                {arrayFields.map((element,index) => {
                   return <Signup
                    field={arrayFields[index]}
                    type={arrayFieldstype[index]}
                    key={index} />
                })}
                <button type="submit">Submit</button>
            </div>

        );
            
        
    }
   if(SignIndetails){
            detailsignIn=(
              <div>
                <SignIn />
                <button type="submit">Submit</button>
              </div>
              
            )
    }
    
  return (
    <div className="App">
      <button type="button" onClick={showsignup} >Signup</button>
      {detailsignup}

      <button type="button" onClick={showsignIn} >SignIn</button>
      {detailsignIn}
      

    </div>
  );
}

export default app1;
