import React from 'react';
import './Dashboard.css';


export const Textinput = (props) =>{
   
   return(
       <div className="heading">
         
          <div className="posttitle" ><p className="headingtext">Type the Text_Input below</p>uername:&nbsp;<input type="text" onChange={props.username}/><br/>Post:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type="textbox" onChange={props.post}/></div>
          
          <button type="button" onClick={props.textstored}>Submit</button>
       </div>
   ); 
}

export const Showtext = (props) =>{
    return(
        <div className="post">
            <div>
                <p>{props.username}</p>
                <p>{props.postcontent}</p>
            </div> 
        </div>
    );
}

export const Message = () => {
    return (
        <div className="dashboard">
            <p>Item has been added </p>
        </div>
        
    );
}
