import React from 'react';
import './Signup.css';


 const Signup = () => { 
    // let messagesignup=['','Signed-Up Successfully','Um...Nocheating',"Password and Confirm Password doesn't match"]
    // let messagesignupindex=0;
    let arrayFieldinput=new Array(6);
    arrayFieldinput=[' ',' ',' ',' ',' ', ' '];
    let inputtyped=new Array(6);

    const Inputtyped=(e,index)=>{
        inputtyped[index]=e.target.value
    }

    const pushinginputtyped = (index)=>{
       
       index===0?arrayFieldinput[0]='': arrayFieldinput[index]=inputtyped[index-1];
    }  
    const verifysignup = () => {
        pushinginputtyped(5);
        if(((arrayFieldinput[4]=== arrayFieldinput[3])&&(arrayFieldinput[5]!==' '&&arrayFieldinput[3]!==' '))||(arrayFieldinput[1]!==' '&&arrayFieldinput[2]!==' '&&arrayFieldinput[3]!==' '&&arrayFieldinput[4]!==' '&&arrayFieldinput[5]!==' ')){
          alert('Signed-Up Successfully');
          console.log(document.getElementById('Name').value);
          console.log(document.getElementById('Email-Id').value);
          
        }
        else{
            alert('Invalid Credentials,Try again');
            console.log(document.getElementById('Name').value);
            console.log(document.getElementById('Phone number').value);
        }
    }
      
        
   
    
    return (
        <div >
         <div>Note: Do not use Cookies or anything stored by the browser , enter all the values manully</div>
         <form onSubmit={verifysignup}>
         <div className="signup">
         <p className="text">Name  </p>
         <div className="inputbox">:&nbsp;<input id="Name" type="text" onChange={(e)=>{Inputtyped(e,0)}} onClick={()=>{pushinginputtyped(0)}}/> </div>
         <p className="text">Email-Id </p>
         <div className="inputbox">:&nbsp;<input id="Email-Id" type="email" onChange={(e)=>{Inputtyped(e,1)}} onClick={()=>{pushinginputtyped(1)}}/> </div>
         <p className="text">Phone number </p>
         <div className="inputbox">:&nbsp;<input id="Phone number" type="number" pattern=".{10,}" onChange={(e)=>{Inputtyped(e,4)}} onClick={()=>{pushinginputtyped(4)}}/> </div>
         <p className="text">Password </p>
         <div className="inputbox">:&nbsp;<input id="Password"  type="password" onChange={(e)=>{Inputtyped(e,2)}} onClick={()=>{pushinginputtyped(2)}}/> </div>
         <p className="text">Confirm Password </p>
         <div className="inputbox">:&nbsp;<input  id="Confirm Password" type="password" onChange={(e)=>{Inputtyped(e,3)}} onClick={()=>{pushinginputtyped(3)}}/> </div>
         
         </div>
         <div className="btnsignup" ><input type="submit"/></div>
         </form>
         <div></div>
        </div>
       
    );

}
export default Signup;
