import React from 'react';
import Signup from './Signup.js';
import SignIn from './SignIn.js';
import './App.css';

function App() {

    const arrayFields=[["Email Id","email"],["Name","text"],["Phone no.","number"],["Password","password"],["Confirm Password","password"]];
    let Signupdetails=true;
    let SignIndetails=true;
    let detailsignup=null;
    let detailsignIn=null;
    const showsignup = () => {
        const currentSignupdetails=Signupdetails;
        Signupdetails=!currentSignupdetails;
    } 
    const showsignIn = () => {
      const currentSignIndetails=SignIndetails;
      SignIndetails=!currentSignIndetails;
  } 
    if(Signupdetails){
        detailsignup=(
            <div>
                {arrayFields.map((element,index) => {
                   return <Signup
                    field={arrayFields[index]}
                    type={arrayFieldstype[index]}
                    key={index} />
                })}
                <button type="submit">Submit</button>
            </div>

        )


    }
   else{
            detailsignIn=(
              <div>
                <SignIn />
                <button type="submit">Submit</button>
              </div>

            )
    }

  return (
    <div className="App">
      <button type="button" onClick={showsignup} >Signup</button>
      {detailsignup}
      <button type="button" onClick={showsignIn} >SignIn</button>
      {detailsignIn}


    </div>
  );
}

export default App;
