import React,{Component} from 'react';
import Signup from './Signup.js';
import SignIn from './SignIn.js';
import './App.css';

class App extends Component {

    state={
       arrayvalue:["name"],
       arrayFields:["Email Id","Name","Phone no.","Password","Confirm Password"],
       arrayFieldstype:["email","text","number","password","password"],
       Signupdetails:false,
       SignIndetails:false,



  }
  showsignup = () => {
    console.log(this.state.Signupdetails);
        const currentSignupdetails=this.state.Signupdetails;
        this.setState({Signupdetails:!currentSignupdetails});
    } 

   showsignIn = () => {
      const currentSignIndetails=this.state.SignIndetails;
      this.setState({SignIndetails:!currentSignIndetails});
  } 
  verifyingtool = (e,index) => {
    this.setState({input:e.target.value});
    let {arrayvalue,input}=this.state;
    arrayvalue.push(input);
    console.log(this.state.input);
  }
  showvalue =() =>{
    console.log(this.state.input);
  }
  render(){

    let detailsignup=null;
    let detailsignIn=null;
    if(this.state.Signupdetails){
        detailsignup=(
            <div>
                {this.state.arrayFields.map((element,index) => {
                   return <Signup
                    field={this.state.arrayFields[index]}
                    type={this.state.arrayFieldstype[index]}
                    changed={ this.verifyingtool}
                    key={index} />
                })}
                <button type="submit" onClick={this.showvalue}>Submit</button>
            </div>

        );


    }
   if(this.state.SignIndetails){
            detailsignIn=(
              <div>
                <SignIn />
                <button type="submit">Submit</button>
              </div>

            )
    }

  return (
    <div className="App">
      <button type="button" onClick={this.showsignup} >Signup</button>
      {detailsignup}
      <button type="button" onClick={this.showsignIn} >SignIn</button>
      {detailsignIn}


    </div>
  );
}}

export default App;
