import React from 'react';
import './Dashboard.css';


export const Textinput = (props) =>{
   return(
       <div className="heading">
          <p>Type the Text_Input below</p>
          <div>uername<input type="text" onChange={props.username}/></div>
          <div>Post<input type="textbox" onChange={props.post}/></div>
          <button type="button" onClick={props.textstored}>Submit</button>
       </div>
   ); 
}

export const Showtext = (props) =>{
    return(
        <div className="post">
        <p>{props.username}</p>
        <p>{props.postcontent}</p>
         
        </div>
    );
}

export const Message = () => {
    return (
        <div className="dashboard">
            <p>Item has been added </p>
        </div>
        
    );
}
